query resourcesConnection(
  $first:Int
  $skip:Int
  $where:  ResourceWhereInput
  $orderBy:  ResourceOrderByInput
){
  objectsConnection: resourcesConnection(
    orderBy: $orderBy
    first: $first
    skip: $skip
    where: $where
  ){
    aggregate{
      count
    }
    edges{
      node{
        ...topic
        CreatedBy{
      		...user
        }
        Comments(
          orderBy: id_ASC
        ){
          id
          updatedAt
          CreatedBy{
            ...user
          }
        }
        Blog{
          id
          name
          longtitle
          uri
        }
        Tags{
          Tag{
            id
            name
          }
        }
      }
    }
  }
}

